import React from 'react';

function DefinitionCard({ result, onSuggestionClick }) {
  if (!result) return null;

  if (result.error) {
    return (
      <div className="definition-card error">
        <h2>‚ùå Error</h2>
        <p>{result.error}</p>
      </div>
    );
  }

  if (!result.found) {
    return (
      <div className="definition-card not-found">
        <h2>ü§î Word Not Found</h2>
        <p>Sorry, we couldn't find "<strong>{result.word}</strong>" in our dictionary.</p>
        
        {result.suggestions && result.suggestions.length > 0 && (
          <div className="suggestions">
            <h3>üí° Did you mean:</h3>
            <div className="suggestion-buttons">
              {result.suggestions.map((suggestion, index) => (
                <button
                  key={index}
                  onClick={() => onSuggestionClick(suggestion)}
                  className="suggestion-btn"
                >
                  {suggestion}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>
    );
  }

  return (
    <div className="definition-card">
      <h2>{result.word}</h2>

      {result.definitions.length > 0 ? (
        result.definitions.map((def, index) => (
          <div key={index} className="definition-entry">
            <p><strong>Type:</strong> {def.type}</p>
            <p><strong>Definition:</strong> {def.definition}</p>
            {def.example && <p><strong>Example:</strong> {def.example}</p>}
          </div>
        ))
      ) : (
        <p>No definitions found.</p>
      )}

      {result.image && (
        <div className="image-preview">
          <img src={result.image} alt={result.word} />
        </div>
      )}

      {result.suggestions.length > 0 && (
        <div className="suggestions">
          <h4>Did you mean?</h4>
          <ul>
            {result.suggestions.map((sug, i) => (
              <li key={i}>
                <button onClick={() => onSuggestionClick(sug)}>
                  {sug}
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default DefinitionCard;
